import React, { useState } from "react";

const useAddCards = () => {
  const [isClick, setIsClick] = useState(false);

  const addCard = () => {
    setIsClick(true);
  };
  const handleSubmit = (e) => {
    e.preventDefault();
  };
  return { isClick, addCard, handleSubmit };
};

export default useAddCards;
