import React, { useEffect, useState } from "react";
import axios from "axios";
import useGetUser from "../../hooks/UserHooks/useGetUser"; // Import the hook
import styleFavCards from "./FavoritCards.module.css"
const FavoritCards = () => {
  const [cards, setCards] = useState([]);
  const { userInformation } = useGetUser(); // Destructure userInformation

  useEffect(() => {
    const fetchCards = async () => {
      try {
        const response = await axios.get('https://monkfish-app-z9uza.ondigitalocean.app/bcard2/cards');
        if (userInformation) {
          const favoriteCards = response.data.filter(card => card.likes.includes(userInformation._id));
          setCards(favoriteCards);
        }
      } catch (error) {
        console.error(error);
      }
    };
    fetchCards();
  }, [userInformation]);

  return (
    <div className="bodyAbout">
      <h1>Favorite Cards</h1>
      <div className={styleFavCards.frontCard}>
      <ul>
        {cards.map((card) => (
          <li key={card._id}>
            <img src={card.image.url} alt={card.image.alt} />
            <h2>{card.title}</h2>
            <p>{card.subtitle}</p>
            {card.address && card.address.country && <p>{card.address.country}</p>}
          </li>
        ))}
      </ul>
      </div> 
     
    </div>
  );
};

export default FavoritCards;
