import React, { useState } from "react";
import useUserApi from "../../hooks/useUserApi";
import "./Register.css";

const SignInRegister = () => {
  const [isSignIn, setIsSignIn] = useState(false);
  const [users, loading, error] = useUserApi();
  const [formData, setFormData] = useState({
    email: "",
    password: "",
    confirmPassword: "",
    phone: "",
    name: "",
  });
  const [formError, setFormError] = useState(null);

  const handleSignInClick = () => {
    setIsSignIn(false);
  };

  const handleSignUpClick = () => {
    setIsSignIn(true);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      // כאן תוכל להוסיף את קוד השליחה לשרת
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const validateForm = () => {
    if (
      !formData.email ||
      !formData.password ||
      !formData.confirmPassword ||
      !formData.phone ||
      !formData.name
    ) {
      setFormError("All fields are required");
      return false;
    }
    if (formData.password !== formData.confirmPassword) {
      setFormError("Passwords do not match");
      return false;
    }
    // הוסף בדיקות נוספות לפי הדרישות שלך, לדוגמה באמצעות רגקסים
    return true;
  };

  return (
    <div className="body-img">
      <div className={`container ${isSignIn ? "right-panel-active" : ""}`}>
        <div className="container__form container--signup">
          <form
            action="#"
            className="form formScroll"
            id="form1"
            onSubmit={handleSubmit}
          >
            <h2 className="form__title">Sign Up</h2>
            <input
              type="text"
              name="name"
              placeholder="Name"
              className="input"
              onChange={handleChange}
            />
            <input
              type="text"
              name="email"
              placeholder="Email"
              className="input"
              onChange={handleChange}
            />
            <input
              type="password"
              name="password"
              placeholder="Password"
              className="input"
              onChange={handleChange}
            />
            <input
              type="password"
              name="confirmPassword"
              placeholder="Confirm Password"
              className="input"
              onChange={handleChange}
            />
            <input
              type="text"
              name="phone"
              placeholder="Phone"
              className="input"
              onChange={handleChange}
            />
            <button className="btn">Sign Up</button>
            {formError && <div className="error">{formError}</div>}
          </form>
        </div>
        <div className="container__overlay">
          <div className="overlay">
            <div className="overlay__panel overlay--left">
              <button className="btn" id="signIn" onClick={handleSignInClick}>
                Sign In
              </button>
            </div>
            <div className="overlay__panel overlay--right">
              <button className="btn" id="signUp" onClick={handleSignUpClick}>
                Sign Up
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SignInRegister;
