import React, { useContext, useEffect, useState } from "react";
import { UserContext } from "../hooks/userContextApp";
import axios from "axios";
import useGetUser from "../hooks/useGetUser";

function SandBox() {
  const { userInformation } = useContext(UserContext);
  const token = localStorage.getItem("token");
  const [allUsers, setAllUsers] = useState([]);

  useGetUser(); // השתמש ב-hook כאן

  useEffect(() => {
    const fetchAllUsers = async () => {
      if (!userInformation || !userInformation.isAdmin) return;

      const configAllUser = {
        method: 'get',
        maxBodyLength: Infinity,
        url: 'https://monkfish-app-z9uza.ondigitalocean.app/bcard2/users',
        headers: { 
          'x-auth-token': token
        }
      };

      try {
        const response = await axios(configAllUser);
        setAllUsers(response.data);
      } catch (error) {
        console.error("Error fetching all users data:", error);
      }
    };

    fetchAllUsers();
  }, [userInformation, token]);

  return (
    <div style={{ paddingTop: "100px" }}>
      {userInformation && userInformation.name ? (
        <>
          <p>{`Hello: ${userInformation.name.first}`}</p>
          {userInformation.isAdmin && (
            <div>
              <h2>All Users:</h2>
              <ul>
                {allUsers.map(user => (
                  <li key={user._id}>{user.name}</li>
                ))}
              </ul>
            </div>
          )}
        </>
      ) : (
        <p>Loading...</p>
      )}
      <p>hello</p>
    </div>
  );
}

export default SandBox;
