// Cards.jsx
import React, { useState } from "react";
import useCardsApi from "../../hooks/useCardsApi";
import styleCards from "./Cards.module.css";
import useAddCards from "./useAddCards";

const Cards = () => {
  const [cards, loading, error] = useCardsApi();
  const { isClick, addCard, handleSubmit } = useAddCards();

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  if (!cards || cards.length === 0) {
    return <div>No cards found</div>;
  }

  return (
    <div className={styleCards.container_cards}>
      <div className={styleCards.frontCard}>
        <h1>Cards</h1>
        <ul className={styleCards.listOfCards}>
          {cards.slice(0, 3).map((card) => (
            <li className={styleCards.item} key={card._id}>
              <img
                className={styleCards.imgCards}
                src={card.image.url}
                alt={card.alt}
              />
              <h2>{card.title}</h2>
              <p className={styleCards.p}> {card.subtitle}</p>
              <p className={styleCards.p}>{card.address.country}</p>
            </li>
          ))}
        </ul>
        <button onClick={addCard}>+</button>
        {isClick && <form onSubmit={handleSubmit}>
        <input type="text" name="title" placeholder="Title required" required />
        <input type="text" name="subtitle" placeholder="Subtitle required" required />
        <input type="text" name="description" placeholder="Description required" required />
        <input type="tel" name="phone" placeholder="Phone required" required />
        <input type="email" name="email" placeholder="Email required" required />
        <input type="url" name="web" placeholder="Web (URL for the website)" />
        <input type="url" name="imageUrl" placeholder="Image URL" />
        <input type="text" name="imageAlt" placeholder="Image Alt" />
        <input type="text" name="city" placeholder="City required" required />
        <input type="text" name="country" placeholder="Country required" required />
        <input type="text" name="state" placeholder="State" />
        <input type="text" name="street" placeholder="Street required" required />
        <input type="text" name="houseNumber" placeholder="House Number required" required />
        <input type="text" name="zip" placeholder="Zip required" required />
          </form>}
      </div>
    </div>
  );
};

export default Cards;
