// frontend/src/components/ImageUpload.js
import React, { useState } from "react";
import axios from "axios";

const ImageUpload = () => {
  const [selectedFiles, setSelectedFiles] = useState([]); // To store multiple images
  const [message, setMessage] = useState("");

  // Handle folder selection
  const handleFileChange = (e) => {
    setSelectedFiles(e.target.files); // Store the selected files in state
  };

  // Handle folder upload
  const handleUpload = async (e) => {
    e.preventDefault();
    if (selectedFiles.length === 0) {
      setMessage("Please upload a folder with images.");
      return;
    }

    const formData = new FormData();
    for (let file of selectedFiles) {
      formData.append("images", file); // Append all images to formData
    }

    try {
      const response = await axios.post(
        "http://localhost:5000/upload-folder", // This endpoint uploads multiple images
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );
      setMessage(response.data.message);
    } catch (error) {
      console.error(error);
      setMessage("Error uploading images");
    }
  };

  return (
    <div style={{ paddingTop: "100px" }}>
      <h1>Upload Event Folder</h1>
      <form onSubmit={handleUpload}>
        <input
          type="file"
          webkitdirectory="true"
          multiple
          onChange={handleFileChange}
        />
        <button type="submit">Upload</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default ImageUpload;
