// frontend/src/components/EventImageUpload.js
import React, { useState, useEffect } from "react";
import axios from "axios";

const EventImageUpload = () => {
  const [images, setImages] = useState([]);
  const [uploadedImages, setUploadedImages] = useState([]);
  const [message, setMessage] = useState("");

  // פונקציה לשליפת כל התמונות מהשרת
  useEffect(() => {
    const fetchAllImages = async () => {
      try {
        const response = await axios.get("http://localhost:5000/all-images");
        setUploadedImages(response.data);
      } catch (error) {
        console.error("Error fetching images:", error);
      }
    };

    fetchAllImages();
  }, []);

  const handleFileChange = (e) => {
    setImages(e.target.files);
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (images.length === 0) {
      setMessage("Please select images to upload.");
      return;
    }

    const formData = new FormData();
    for (let i = 0; i < images.length; i++) {
      formData.append("images", images[i]);
    }

    try {
      const response = await axios.post(
        "http://localhost:5000/upload-images",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );
      setMessage(response.data.message);

      // עדכון רשימת התמונות לאחר העלאה מוצלחת
      const updatedImages = await axios.get("http://localhost:5000/all-images");
      setUploadedImages(updatedImages.data);
    } catch (error) {
      console.error("Error uploading images:", error);
      setMessage("Error uploading images.");
    }
  };

  return (
    <div style={{ paddingTop: "100px" }}>
      <h1>Upload Event Images</h1>
      <form onSubmit={handleUpload}>
        <input
          type="file"
          multiple
          accept="image/*"
          onChange={handleFileChange}
        />
        <button type="submit">Upload Images</button>
      </form>
      {message && <p>{message}</p>}

      {/* הצגת התמונות שהועלו */}
      <h2>Uploaded Images</h2>
      <div style={{ display: "flex", flexWrap: "wrap" }}>
        {uploadedImages.map((image, index) => (
          <div key={index} style={{ margin: "10px" }}>
            <img
              src={`http://localhost:5000/images/${encodeURIComponent(
                image.filename
              )}`}
              alt={`Event ${index + 1}`}
              style={{ width: "150px", height: "150px", objectFit: "cover" }}
            />

            <p>{image.filename}</p>
            <p>Type: {image.type}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default EventImageUpload;
