import React, { useState, useEffect } from "react";
import axios from "axios";

const SelfieUpload = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [message, setMessage] = useState("");
  const [uploadedSelfie, setUploadedSelfie] = useState(null); // Store uploaded selfie filename

  // Use useEffect to load saved selfie from localStorage
  useEffect(() => {
    const savedSelfie = localStorage.getItem("uploadedSelfie");
    if (savedSelfie) {
      setUploadedSelfie(savedSelfie); // Load the selfie from localStorage if it exists
    }
  }, []);

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setSelectedFile(file);
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!selectedFile) {
      setMessage("Please upload a selfie.");
      return;
    }

    const formData = new FormData();
    formData.append("image", selectedFile);

    try {
      const response = await axios.post(
        "http://localhost:5000/upload-selfie",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );

      const { filename } = response.data; // Expect filename in the response
      setMessage(response.data.message);
      setUploadedSelfie(filename); // Store the filename in state

      // Save the filename in localStorage to persist across reloads
      localStorage.setItem("uploadedSelfie", filename);
    } catch (error) {
      console.error(error);
      setMessage("Error uploading selfie");
    }
  };

  return (
    <div style={{ paddingTop: "100px" }}>
      <h1>Upload Your Selfie</h1>
      <form onSubmit={handleUpload}>
        <input type="file" accept="image/*" onChange={handleFileChange} />
        <button type="submit">Upload</button>
      </form>
      {message && <p>{message}</p>}

      {/* Display the uploaded selfie */}
      {uploadedSelfie && (
        <div>
          <h2>Uploaded Selfie</h2>
          <img
            src={`http://localhost:5000/selfies/${encodeURIComponent(
              uploadedSelfie
            )}`}
            alt={`Uploaded selfie: ${uploadedSelfie}`}
            style={{ width: "150px", height: "150px", objectFit: "cover" }}
          />
        </div>
      )}
    </div>
  );
};

export default SelfieUpload;
