import { useEffect, useState } from 'react';
import axios from 'axios';

const useGetOneCard = (cardId) => {
  const [cardData, setCardData] = useState(null);
  const [error, setError] = useState(null);
  const token = localStorage.getItem('token'); // אם יש צורך בטוקן לאוטנטיקציה

  useEffect(() => {
    const fetchCard = async () => {
      if (!cardId) return; // אם לא מסופק מזהה לכרטיס, לא להמשיך

      const config = {
        method: 'get',
        maxBodyLength: Infinity,
        url: `https://monkfish-app-z9uza.ondigitalocean.app/bcard2/cards/${cardId}`,
        headers: {
          'Content-Type': 'application/json',
          // אפשר להוסיף הכנסת טוקן אם נדרש
          // 'x-auth-token': token,
        },
      };

      try {
        const response = await axios(config);
        setCardData(response.data);
      } catch (error) {
        setError(error);
      }
    };

    fetchCard();

  }, [cardId]);

  return { cardData, error };
};

export default useGetOneCard;
