import React from "react";

const Child = ({ name, fruitList, updateFruit, updateVeg }) => {
  const newFruit = () => {
    const addFruit = [...fruitList, "Orange"];
    updateFruit(addFruit);
  };
  const newVeg = (updateVeg) => {
    const addVeg = [...fruitList, "NewVeg"];
    updateVeg(addVeg);
  };
  const newWord = () => {
    const addWord = [...fruitList, "Word"];
  };

  return (
    <div>
      <h1>{name}</h1>
      <h2>Fruits:</h2>
      <ul>
        {fruitList.map((fruit, index) => (
          <li key={index}>{fruit}</li>
        ))}
      </ul>
      {/* Add text to the button */}
      <button onClick={newFruit}>Add Orange</button>
      <button onClick={newVeg}>new Veg</button>
      <button onClick={newWord}>new Veg</button>
    </div>
  );
};

export default Child;
