import React, { useState, useEffect } from "react";
import axios from "axios";

const SelfieUpload = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [message, setMessage] = useState("");
  const [uploadedSelfie, setUploadedSelfie] = useState(null);
  const [imageData, setImageData] = useState(null);
  const [matchedImages, setMatchedImages] = useState([]); // שמירת התמונות המותאמות לאחר זיהוי הפנים

  // Fetch selfie image from the server after upload
  useEffect(() => {
    if (uploadedSelfie) {
      const fetchSelfie = async () => {
        try {
          const response = await axios.get(
            `http://localhost:5000/api/selfies/${encodeURIComponent(
              uploadedSelfie
            )}`,
            { responseType: "blob" }
          );
          const imageUrl = URL.createObjectURL(response.data);
          setImageData(imageUrl);
        } catch (error) {
          console.error("Error fetching selfie:", error);
          setMessage("Error fetching selfie");
        }
      };

      fetchSelfie();
    }
  }, [uploadedSelfie]);

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setSelectedFile(file);
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!selectedFile) {
      setMessage("Please upload a selfie.");
      return;
    }

    const formData = new FormData();
    formData.append("image", selectedFile);

    try {
      const response = await axios.post(
        "http://localhost:5000/api/selfies/upload",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );

      setMessage(response.data.message);
      setUploadedSelfie(selectedFile.name); // שמירת שם הקובץ להעלאה
    } catch (error) {
      console.error("Error uploading selfie:", error);
      setMessage("Error uploading selfie");
    }
  };

  // פונקציה לטיפול בזיהוי הפנים מול תמונות האירוע
  const handleFaceRecognition = async () => {
    if (!uploadedSelfie) {
      setMessage("Please upload a selfie first.");
      return;
    }

    try {
      const response = await axios.post("http://localhost:5000/match-faces", {
        sourceImageFilename: uploadedSelfie, // תמונת הסלפי
      });

      setMatchedImages(response.data.matches); // שמירת התמונות המותאמות בזיכרון הקומפוננטה
    } catch (error) {
      console.error("Error during face recognition:", error);
      setMessage("Error during face recognition");
    }
  };

  return (
    <div style={{ paddingTop: "100px" }}>
      <h1>Upload Your Selfie</h1>
      <form onSubmit={handleUpload}>
        <input type="file" accept="image/*" onChange={handleFileChange} />
        <button type="submit">Upload</button>
      </form>
      {message && <p>{message}</p>}

      {/* הצגת הסלפי שהועלה */}
      {imageData && (
        <div>
          <h2>Uploaded Selfie</h2>
          <img
            src={imageData}
            alt={`Uploaded selfie: ${uploadedSelfie}`}
            style={{ width: "150px", height: "150px", objectFit: "cover" }}
          />
        </div>
      )}

      {/* כפתור לזיהוי פנים */}
      {uploadedSelfie && (
        <button onClick={handleFaceRecognition}>Start Face Recognition</button>
      )}

      {/* הצגת התמונות שהותאמו */}
      {matchedImages.length > 0 && (
        <div>
          <h2>Matched Images</h2>
          <div style={{ display: "flex", flexWrap: "wrap" }}>
            {matchedImages.map((image, index) => (
              <div key={index} style={{ margin: "10px" }}>
                <img
                  src={`http://localhost:5000/images/${encodeURIComponent(
                    image.filename
                  )}`}
                  alt={image.filename}
                  style={{
                    width: "150px",
                    height: "150px",
                    objectFit: "cover",
                  }}
                />
                <p>{image.filename}</p>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default SelfieUpload;
