import React, { useState, useEffect } from "react";
// import { Link } from "react-router-dom";
import SunIcon from "../images/Sunny.png";
import MoonIcon from "../images/Moon.png";
const NavBar = () => {
  const [bgColor, setBgColor] = useState("white");

  const handleSunClick = () => {
    setBgColor("yellow"); // צבע רקע שמתאים לאייקון השמש
  };

  const handleMoonClick = () => {
    setBgColor("black"); // צבע רקע שמתאים לאייקון הירח
  };

  useEffect(() => {
    document.body.style.backgroundColor = bgColor;
  }, [bgColor]);

  return (
    <nav>
      <ul>
        <li>
          <img src={SunIcon} alt="Sun Icon" onClick={handleSunClick} />
          <img src={MoonIcon} alt="Moon Icon" onClick={handleMoonClick} />
        </li>
      </ul>
    </nav>
  );
};

export default NavBar;
