import React from "react";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import SunIcon from "./images/Sunny.png";
import MoonIcon from "./images/Moon.png";
import Register from "./component/Register";
import SearchIcon from "./images/SearchIcon.png";
import MyCards from "./component/MyCards";
import FavoritCards from "./component/FavoritCards";
import Login from "./component/Login";
import { ThemeProvider, ThemeContext } from "./hooks/DarkModeContext"; // ייבוא הקונטקסט
import "./App.css";

function App() {
  return (
    <ThemeProvider>
      <Router>
        <div>
          <NavBar />
          <Routes>
            <Route path="/about" />
            <Route path="/myCards" element={<MyCards />} />
            <Route path="/favCards" element={<FavoritCards />} />
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<SignInRegister />} />
          </Routes>
        </div>
      </Router>
    </ThemeProvider>
  );
}

export default App;
