import React, { createContext, useState, useEffect } from "react";
import axios from "axios";

export const UserContext = createContext();

export const UserProvider = ({ children }) => {
  const [userInformation, setUserInformation] = useState(null);

  useEffect(() => {
    // בדיקת אם יש טוקן ב-localStorage
    const token = localStorage.getItem("token");

    // נבצע את הקריאה ל-API רק אם יש טוקן
    if (token) {
      axios
        .get("http://localhost:5000/api/users/me", {
          withCredentials: true, // אם אתה משתמש ב-Cookies
        })
        .then((response) => {
          setUserInformation(response.data);
        })
        .catch((error) => {
          console.error("Error:", error);
          setUserInformation(null); // אם יש שגיאה או אין טוקן, נמחק את המידע
        });
    }
  }, []); // יבוצע פעם אחת כאשר הקומפוננטה נטענת

  return (
    <UserContext.Provider value={{ userInformation, setUserInformation }}>
      {children}
    </UserContext.Provider>
  );
};
