import React, { useEffect, useState } from "react";
import axios from "axios";

const FavoritCards = () => {
  const [cards, setCards] = useState([]);
  const userId = localStorage.getItem("user_id");

  useEffect(() => {
    const fetchCards = async () => {
      try {
        const response = await axios.get('https://monkfish-app-z9uza.ondigitalocean.app/bcard2/cards');
        const favoriteCards = response.data.filter(card => card.likes.includes(userId));
        setCards(favoriteCards);
      } catch (error) {
        console.error(error);
      }
    };
    fetchCards();
  }, [userId]);

  return (
    <div className="bodyAbout">
      <h1>Favorite Cards</h1>
      <ul>
        {cards.map((card) => (
          <li key={card._id}>
            <img src={card.image.url} alt={card.image.alt} />
            <h2>{card.title}</h2>
            <p>{card.subtitle}</p>
            {card.address && card.address.country && <p>{card.address.country}</p>}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default FavoritCards;
