import { UserContext } from "../hooks/userContextApp";
import axios from "axios";
import useGetUser from "../hooks/useGetUser";

function SandBox() {
  const { userInformation, setUserInformation } = useContext(UserContext);
  const token = localStorage.getItem("token");
  useGetUser(); // השתמש ב-hook כאן
useEffect(()=>{
  const fetchUser = async () => {
    const configAllUser = {
      method: 'get',
    maxBodyLength: Infinity,
      url: 'https://monkfish-app-z9uza.ondigitalocean.app/bcard2/users',
      headers: { 
        'x-auth-token':token
      }
  }
  try {
    const response = await axios(configAllUser);
    setUserInformation(response.data)

  } catch (error) {
    console.error("Error fetching user data:", error);

    
  }
 };
 fetchUser();
},[userInformation,token])

  return (
    <div style={{ paddingTop: "100px" }}>
      {userInformation && userInformation.name ? (
        <p>{`Hello: ${userInformation.name.first}`}</p>
      ) : (
        <p>Loading...</p>
      )}
      <p>hello</p>
    </div>
  );
}

export default SandBox;
