import React from "react";
import useCardsApi from "../../hooks/useCardsApi";
import useAddCards from "./AddCard/useAddCards";
import Loading from "../Loading/Loding";
import FormComponent from "./AddCard/FormComponent";
import styleCards from "./Cards.module.css";

const Cards = () => {
  const [cards, loading, error, setCards] = useCardsApi(); // שימוש בפונקציה שמחזירה גם פונקציה לעדכון הרשימה

  const { isClick, OpenAndCloseCard, handleChange, handleSubmit, formValid, formData, closeForm } = useAddCards();

  const handleAddCard = (newCard) => {
    // עדכון הקורא פונקציה
    setCards((prevCards) => [newCard, ...prevCards]);
  };

  if (loading) {
    return <Loading />;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  if (!cards || cards.length === 0) {
    return <div>No cards found</div>;
  }

  return (
    <div>
      <div className="bodyAbout">
        <div className={styleCards.container_cards}>
          <div className={styleCards.frontCard}>
            <h1 className={styleCards.h1}>Cards</h1>
            <ul className={styleCards.listOfCards}>
              {cards.slice(0, 3).map((card) => (
                <li className={styleCards.item} key={card._id}>
                  <img className={styleCards.imgCards} src={card.image.url} alt={card.alt} />
                  <h2>{card.title}</h2>
                  <p className={styleCards.p}> {card.subtitle}</p>
                  <p className={styleCards.p}>{card.address.country}</p>
                </li>
              ))}
            </ul>
            <button onClick={OpenAndCloseCard}>+</button>
            {isClick && (
              <FormComponent
                handleChange={handleChange}
                handleSubmit={(newCard) => {
                  handleSubmit(newCard);
                  handleAddCard(newCard); // עדכון הקורא פונקציה
                }}
                formValid={formValid}
                formData={formData}
                closeForm={closeForm}
              />
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Cards;
