import React, { useState, useEffect } from "react";
import StyleLogin from "./login.module.css";

const SimpleLogin = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [token, setToken] = useState("");

  useEffect(() => {
    const fetchData = async () => {
      const userData = {
        username,
        password,
      };

      try {
        const response = await fetch(
          "https://monkfish-app-z9uza.ondigitalocean.app/bcard2/users/6559f2dbdedf2db2b52bde42",
          {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify(userData),
          }
        );

        if (response.ok) {
          const data = await response.json();
          const jwtToken = data.token;
          setToken(jwtToken);
          console.log("User logged in successfully:", data);
        } else {
          console.error("Failed to log in:", response.statusText);
        }
      } catch (error) {
        console.error("Error:", error);
      }
    };

    fetchData();
  }, [username, password]);

  const handleSubmit = (e) => {
    e.preventDefault();
    // No need to make a fetch request here, it's handled by useEffect
  };

  return (
    <div className={StyleLogin.containerForm}>
      <form className={StyleLogin.form} onSubmit={handleSubmit}>
        <h1 className={StyleLogin.title}>Simple Login</h1>

        <div>
          <label>Username:</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div>
          <label className={StyleLogin.label}>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Login</button>
      </form>
      {token && <p>JWT Token: {token}</p>}
    </div>
  );
};

export default SimpleLogin;
