import { useEffect } from "react";
import axios from "axios";

const useLikeUnlike = (cardId) => {
  useEffect(() => {
    const likeUnlike = async () => {
      try {
        const token = localStorage.getItem("token");
        let configLikes = {
          method: "patch",
          maxBodyLength: Infinity,
          url: `https://monkfish-app-z9uza.ondigitalocean.app/bcard2/cards/${cardId}`,
          headers: {
            "x-auth-token": token,
          },
        };

        await axios(configLikes);
        console.log("Successfully liked/unliked card");
      } catch (error) {
        console.error("Error liking/unliking card:", error.message);
      }
    };

    likeUnlike();
  }, [cardId]);
};

export default useLikeUnlike;
