import React, { useEffect } from "react";
import "../Style/SignInRegister.css";

const SignInRegister = ({ onRegister }) => {
  useEffect(() => {
    const container = document.querySelector(".container");
    const signInBtn = document.getElementById("signIn");
    const signUpBtn = document.getElementById("signUp");
    const firstForm = document.getElementById("form1");
    const secondForm = document.getElementById("form2");

    // הפעלת פעולות JavaScript רק פעם אחת בעת טעינת הרכיב
    if (container && signInBtn && signUpBtn && firstForm && secondForm) {
      const handleSignInClick = () => {
        container.classList.remove("right-panel-active");
      };

      const handleSignUpClick = () => {
        container.classList.add("right-panel-active");
      };

      const handleSubmit = (e) => {
        e.preventDefault();
        onRegister();
      };

      signInBtn.addEventListener("click", handleSignInClick);
      signUpBtn.addEventListener("click", handleSignUpClick);
      firstForm.addEventListener("submit", handleSubmit);
      secondForm.addEventListener("submit", handleSubmit);

      return () => {
        signInBtn.removeEventListener("click", handleSignInClick);
        signUpBtn.removeEventListener("click", handleSignUpClick);
        firstForm.removeEventListener("submit", handleSubmit);
        secondForm.removeEventListener("submit", handleSubmit);
      };
    }
  }, [onRegister]);

  return (
    <div className="body-img">
      <div className="container right-panel-active">
        <div className="container__form container--signup">
          <form action="#" className="form" id="form1">
            <h2 className="form__title">Sign Up</h2>
            <input type="text" placeholder="User" className="input" />
            <input type="email" placeholder="Email" className="input" />
            <input type="password" placeholder="Password" className="input" />
            <button className="btn">Sign Up</button>
          </form>
        </div>

        <div className="container__form container--signin">
          <form action="#" className="form" id="form2">
            <h2 className="form__title">Sign In</h2>
            <input type="email" placeholder="Email" className="input" />
            <input type="password" placeholder="Password" className="input" />
            <button className="btn">Sign In</button>
          </form>
        </div>

        <div className="container__overlay">
          <div className="overlay">
            <div className="overlay__panel overlay--left">
              <button className="btn" id="signIn">
                Sign In
              </button>
            </div>
            <div className="overlay__panel overlay--right">
              <button className="btn" id="signUp">
                Sign Up
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SignInRegister;
