import React, { useState } from "react";

const Toggle = () => {
  const [isVisible, setIsVisible] = useState(true);
  return (
    <div>
      <h1>Toggle Component</h1>
      {isVisible && <p>Now you see me!</p>}
      <button onClick={() => setIsVisible()}>Toggle Visibility</button>
    </div>
  );
};

export default Toggle;
