// src/hooks/useCardsHooks/useCardsApi.jsx

import { useState, useEffect } from 'react';
import axios from 'axios';

const useCardsApi = () => {
  const [cards, setCards] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Function to fetch cards data from the API
  const fetchCards = async () => {
    try {
      const response = await axios.get('https://monkfish-app-z9uza.ondigitalocean.app/bcard2/cards');
      setCards(response.data);
      setLoading(false);
    } catch (err) {
      setError(err.message);
      setLoading(false);
    }
  };

  // Effect hook to fetch cards data when component mounts
  useEffect(() => {
    fetchCards();
  }, []);

  // Function to like/unlike a card
  const likeCard = async (cardId, token) => {
    try {
      const config = {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      };
      await axios.patch(`https://monkfish-app-z9uza.ondigitalocean.app/bcard2/cards/${cardId}`, {}, config);
      // After successful like, update the list of cards
      await fetchCards(); // Call fetchCards to update the list
    } catch (err) {
      console.error('Error liking card:', err);
      setError(err.message);
    }
  };

  // Return the necessary state and functions for component usage
  return [cards, setCards, loading, error, likeCard, fetchCards];
};

export default useCardsApi;
