import React, { useEffect, useState } from "react";
import axios from "axios";
import styleFindTheOne from "./FoundTheOne.module.css";
import Loading from "../Loading/Loading";
import useGetUser from "../../hooks/UserHooks/useGetUser";
import FavoriteBorderIcon from "@mui/icons-material/FavoriteBorder";
import FavoriteIcon from "@mui/icons-material/Favorite";
import FavoritCards from "../FavoritCards/FavoritCards"; // Import FavoritCards component

const FindTheOne = () => {
  const [cards, setCards] = useState([]);
  const { userInformation } = useGetUser();

  useEffect(() => {
    const fetchCards = async () => {
      try {
        const response = await axios.get('https://monkfish-app-z9uza.ondigitalocean.app/bcard2/cards');
        if (userInformation) {
          const favoriteCards = response.data.filter(card => card.likes.includes(userInformation._id));
          // Add 'liked' property to each card based on user's liked cards
          const updatedCards = response.data.map(card => ({
            ...card,
            liked: favoriteCards.some(favCard => favCard._id === card._id)
          }));
          setCards(updatedCards);
        } else {
          setCards(response.data); // Set cards without 'liked' property if userInformation is not available
        }
      } catch (error) {
        console.error(error);
      }
    };
    fetchCards();
  }, [userInformation]);

  const handleLike = async (cardId, liked) => {
    const token = localStorage.getItem("token");
    try {
      const url = `https://monkfish-app-z9uza.ondigitalocean.app/bcard2/cards/${cardId}`;
      const config = {
        method: 'patch',
        maxBodyLength: Infinity,
        url,
        headers: {
          'x-auth-token': token
        },
      };
      await axios(config);

      const updatedCards = cards.map(c =>
        c._id === cardId ? { ...c, liked } : c
      );
      setCards(updatedCards);

      // Update local storage with new liked cards
      const newLikedCardIds = updatedCards.filter(c => c.liked).map(c => c._id);
      localStorage.setItem("likedCards", JSON.stringify(newLikedCardIds));
    } catch (error) {
      console.error(error);
    }
  };

  if (cards.length === 0) {
    return <Loading />;
  }

  return (
    <div className="bodyFindTheOne">
      <div className={styleFindTheOne.container_cards}>
        <div className={styleFindTheOne.frontCard}>
          <h1>Cards</h1>
          <ul className={styleFindTheOne.listOfCards}>
            {cards.map((card) => (
              <li className={styleFindTheOne.item} key={card._id}>
                <img
                  className={styleFindTheOne.imgCards}
                  src={card.image.url}
                  alt={card.image.alt}
                />
                <h2>{card.title}</h2>
                <p className={styleFindTheOne.p}>{card.subtitle}</p>
                <p className={styleFindTheOne.p}>{card.address.country}</p>
                <div onClick={() => handleLike(card._id, !card.liked)}>
                  {card.liked ? (
                    <FavoriteIcon style={{ color: "red" }} />
                  ) : (
                    <FavoriteBorderIcon />
                  )}
                </div>
              </li>
            ))}
          </ul>
        </div>
      </div>
      {/* Render FavoritCards component with updated cards */}
      <FavoritCards cards={cards} />
    </div>
  );
};

export default FindTheOne;
