import React from "react";
import useCardsApi from "../../hooks/useCardsHooks/useCardsApi"; // יש לך כבר את ההוק כדי שיפעל

const FavoritCards = () => {
  const [cards, setCards, loading, error] = useCardsApi();

  return (
    <div className="bodyAbout">
      <h1>Favorite Cards</h1>
      <ul>
        {cards.map((card) => (
          <li key={card._id}>
            <img src={card.image.url} alt={card.image.alt} />
            <h2>{card.title}</h2>
            <p>{card.subtitle}</p>
            {card.address && card.address.country && <p>{card.address.country}</p>}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default FavoritCards;
