// UserContextApp.js

import React, { createContext, useState, useEffect, useContext } from "react";

// Create a context object
export const UserContext = createContext();

// Create a provider component
export const UserProvider = ({ children }) => {
  const [userInformation, setUserInformation] = useState(
    JSON.parse(localStorage.getItem("userInformation")) || null
  );

  useEffect(() => {
    const storedUserInfo = localStorage.getItem('userInformation');
    if (storedUserInfo) {
      const parsedUserInfo = JSON.parse(storedUserInfo);
      setUserInformation(parsedUserInfo);
    }
  }, []);

  // Function to login the user
  const loginUser = (userData) => {
    setUserInformation(userData);
    localStorage.setItem('userInformation', JSON.stringify(userData));
  };

  // Function to logout the user
  const logoutUser = () => {
    setUserInformation(null);
    localStorage.removeItem('userInformation');
  };

  return (
    <UserContext.Provider value={{ userInformation, loginUser, logoutUser }}>
      {children}
    </UserContext.Provider>
  );
};
