// hooks/useCardsApi.js
import { useState, useEffect } from "react";
import axios from "axios";
import useLikeUnlike from "./useLikeUnlike"; // Import the useLikeUnlike hook
import {UserContext} from "../UserHooks/userContextApp"

const useCardsApi = () => {
  const [cards, setCards] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { likedCards, setLikedCards } = useContext(UserContext); // Assuming UserContext is accessible here

  useEffect(() => {
    const fetchCards = async () => {
      try {
        const response = await axios.get('https://monkfish-app-z9uza.ondigitalocean.app/bcard2/cards');
        setCards(response.data);
        setLoading(false);
        console.log(response.data);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchCards();
  }, []);

  // Hook to handle liking and unliking
  const handleLikeUnlike = (cardId) => {
    useLikeUnlike(cardId, setLikedCards); // Pass setLikedCards to update likedCards state
  };

  return { cards, loading, error, handleLikeUnlike };
};

export default useCardsApi;
