import React, { useState } from "react";
import axios from "axios";
import "./Register.css";
const SignInRegister = () => {
  const [isSignIn, setIsSignIn] = useState(false);
  const [data, setIsData] = useState({
    name: { first: "", middle: "", last: "" },
    phone: "",
    email: "",
    password: "",
    image: { url: "", alt: "" },
    address: {
      state: "",
      country: "",
      city: "",
      street: "",
      houseNumber: "",
      zip: "",
    },
    isBusiness: false,
    isAdmin: false,
  });
  console.log(data);

  const handleSignInClick = () => {
    setIsSignIn(false);
  };

  const handleSignUpClick = () => {
    setIsSignIn(true);
  };
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    const updatedValue = type === "checkbox" ? checked : value;

    setIsData((prevData) => {
      const keys = name.split(".");
      if (keys.length > 1) {
        return {
          ...prevData,
          [keys[0]]: {
            ...prevData[keys[0]],
            [keys[1]]: updatedValue,
          },
        };
      } else {
        return {
          ...prevData,
          [name]: updatedValue,
        };
      }
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log(data);

    const config = {
      method: "post",
      maxBodyLength: Infinity,
      url: "https://monkfish-app-z9uza.ondigitalocean.app/bcard2/users",
      headers: {},
      data: data,
    };

    try {
      const response = await axios(config);
      console.log(response);
    } catch {}
  };

  return (
    <div className="body-img">
      <div className={`container ${isSignIn ? "right-panel-active" : ""}`}>
        <div className="container__form container--signup">
          <form
            action="#"
            className="form formScroll"
            id="form1"
            onSubmit={handleSubmit}
          >
            <h2 className="form__title">Sign Up</h2>
            <input
              type="text"
              name="name.first"
              placeholder="First Name"
              className="input"
              value={data.name.first}
              onChange={handleChange}
            />
            <input
              type="text"
              name="name.last"
              placeholder="Last Name"
              className="input"
              value={data.name.last}
              onChange={handleChange}
            />
            <input
              name="email"
              type="email"
              placeholder="Email"
              className="input"
              value={data.email}
              onChange={handleChange}
            />
            <input
              name="phone"
              type="text"
              placeholder="Phone"
              className="input"
              value={data.phone}
              onChange={handleChange}
            />
            <input
              type="password"
              name="password"
              placeholder="Password"
              className="input"
              value={data.password}
              onChange={handleChange}
            />
            <input
              name="image.url"
              type="text"
              placeholder="Image Url"
              className="input"
              value={data.image.url}
              onChange={handleChange}
            />
            <input
              name="image.alt"
              type="text"
              placeholder="Image Description"
              className="input"
              value={data.image.alt}
              onChange={handleChange}
            />
            <input
              name="address.state"
              type="text"
              placeholder="Password"
              className="input"
              value={data.address.state}
              onChange={handleChange}
            />
            <input
              name="address.country"
              type="text"
              placeholder="Password"
              className="input"
              value={data.address.country}
              onChange={handleChange}
            />
            <input
              name="address.city"
              type="text"
              placeholder="Password"
              className="input"
              value={data.address.city}
              onChange={handleChange}
            />
            <input
              name="address.houseNumber"
              type="text"
              placeholder="Password"
              className="input"
              value={data.address.houseNumber}
              onChange={handleChange}
            />
            <input
              name="isAdmin"
              checked={data.isAdmin}
              type="checkbox"
              className="checkbox"
              onChange={handleChange}
            />

            <button className="btn">Sign Up</button>
          </form>
        </div>

        <div className="container__form container--signin">
          <form action="#" className="form" id="form2" onSubmit={handleSubmit}>
            <h2 className="form__title">Sign In</h2>
            <input type="email" placeholder="Email" className="input" />
            <input type="password" placeholder="Password" className="input" />
            <button className="btn">Sign In</button>
          </form>
        </div>

        <div className="container__overlay">
          <div className="overlay">
            <div className="overlay__panel overlay--left">
              <button className="btn" id="signIn" onClick={handleSignInClick}>
                Sign In
              </button>
            </div>
            <div className="overlay__panel overlay--right">
              <button className="btn" id="signUp" onClick={handleSignUpClick}>
                Sign Up
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SignInRegister;
