import React, { useEffect, useState } from "react";

import Child from "./Child";

const Father = () => {
  const [fruitList, setFruitList] = useState(["apple", "pear", "banana"]);
  const [showList, setShowList] = useState(false);
  const fruit = ["apple", "pear", "banana"];

  useEffect(() => {
    setShowList(false);
    setFruitList(fruit);
  }, []);

  const updateFruitList = (newFruitList) => {
    setFruitList(newFruitList);
  };

  return (
    <div>
      <h1>Hello, I'm The Father</h1>

      <button onClick={() => setShowList(!showList)}>
        {showList ? "Hide" : "Show"} List
      </button>

      {showList && (
        <Child
          name="Gal"
          fruitList={fruitList}
          updateFruitList={updateFruitList}
        />
      )}
    </div>
  );
};

export default Father;
