import React, { useState, useEffect } from "react";
import axios from "axios";

const SelfieUpload = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [message, setMessage] = useState("");
  const [uploadedSelfie, setUploadedSelfie] = useState(null);
  const [imageData, setImageData] = useState(null);

  // Fetch selfie image from the server after upload using useEffect
  useEffect(() => {
    if (uploadedSelfie) {
      const fetchSelfie = async () => {
        try {
          const response = await axios.get(
            `http://localhost:5000/api/selfies/${encodeURIComponent(
              uploadedSelfie
            )}`,
            { responseType: "blob" } // To handle binary image data
          );
          const imageUrl = URL.createObjectURL(response.data); // Convert blob to URL
          setImageData(imageUrl); // Set the image URL for rendering
        } catch (error) {
          console.error("Error fetching selfie:", error);
          setMessage("Error fetching selfie");
        }
      };

      fetchSelfie(); // Call the function to fetch the selfie
    }
  }, [uploadedSelfie]); // useEffect will run when uploadedSelfie changes

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setSelectedFile(file);
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!selectedFile) {
      setMessage("Please upload a selfie.");
      return;
    }

    const formData = new FormData();
    formData.append("image", selectedFile);

    try {
      const response = await axios.post(
        "http://localhost:5000/api/selfies/upload", // Upload selfie image
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );

      setMessage(response.data.message);
      setUploadedSelfie(selectedFile.name); // Save the filename for display
    } catch (error) {
      console.error("Error uploading selfie:", error);
      setMessage("Error uploading selfie");
    }
  };

  return (
    <div style={{ paddingTop: "100px" }}>
      <h1>Upload Your Selfie</h1>
      <form onSubmit={handleUpload}>
        <input type="file" accept="image/*" onChange={handleFileChange} />
        <button type="submit">Upload</button>
      </form>
      {message && <p>{message}</p>}

      {/* Display the uploaded selfie */}
      {imageData && (
        <div>
          <h2>Uploaded Selfie</h2>
          <img
            src={imageData}
            alt={`Uploaded selfie: ${uploadedSelfie}`}
            style={{ width: "150px", height: "150px", objectFit: "cover" }}
          />
        </div>
      )}
    </div>
  );
};

export default SelfieUpload;
