// frontend/src/components/ImageUpload.js
import React, { useState } from "react";
import axios from "axios";

const ImageUpload = () => {
  const [selectedFile, setSelectedFile] = useState(null); // Single file (ZIP)
  const [message, setMessage] = useState("");

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!selectedFile) {
      setMessage("Please select a ZIP file to upload.");
      return;
    }

    const formData = new FormData();
    formData.append("zipFile", selectedFile);

    try {
      const response = await axios.post(
        "http://localhost:5000/upload-zip",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );
      setMessage(response.data.message);
    } catch (error) {
      console.error(error);
      setMessage("Error uploading ZIP file");
    }
  };

  return (
    <div>
      <h1>Upload Event Images as ZIP</h1>
      <form onSubmit={handleUpload}>
        <input type="file" accept=".zip" onChange={handleFileChange} />
        <button type="submit">Upload</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default ImageUpload;
