// frontend/src/components/SelfieUpload.js
import React, { useState } from "react";
import axios from "axios";
import MatchedImages from "./MatchedImages";

const SelfieUpload = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [message, setMessage] = useState("");
  const [matchingImages, setMatchingImages] = useState([]);

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!selectedFile) {
      setMessage("Please upload a selfie.");
      return;
    }

    const formData = new FormData();
    formData.append("image", selectedFile);

    try {
      const response = await axios.post(
        "http://localhost:5000/upload-selfie",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );

      setMessage(response.data.message);
      setMatchingImages(response.data.matchingImages);
    } catch (error) {
      console.error("Error uploading selfie:", error);
      setMessage("Error uploading selfie.");
    }
  };

  return (
    <div>
      <h1>Upload Your Selfie</h1>
      <form onSubmit={handleUpload}>
        <input type="file" accept="image/*" onChange={handleFileChange} />
        <button type="submit">Upload Selfie</button>
      </form>
      {message && <p>{message}</p>}
      {matchingImages.length > 0 && <MatchedImages images={matchingImages} />}
    </div>
  );
};

export default SelfieUpload;
