import React from "react";
import useUserApi from "../../hooks/UserHooks/useUserApi";
import useGetOneCard from '../../hooks/useCardsHooks/useGetOneCard';
import "./Register.css";

const SignInRegister = () => {
  const { cardData, error } = useGetOneCard('65422172e443ec28a252c27d'); // המזהה של הכרטיס הראשון

  const {
    isSignIn,
    errors,
    data,
    isLoginData,
    handleChange,
    handleSubmit,
    handleSubmit2,
  } = useUserApi();

  if (!cardData) {
    return <p>Loading...</p>; // או אפשר להציג מסך טעינה אחר
  }

  if (error) {
    return <p>Error: {error.message}</p>;
  }

  return (
    <div className="bodyRegister bodyAbout">
      <div className={`container ${isSignIn ? "right-panel-active" : ""}`}>
        <div className="container__form container--signup">
          <form
            action="#"
            className="form formScroll"
            id="form1"
            onSubmit={handleSubmit}
          >
            <h2 className="form__title">Sign Up</h2>
            <input
              type="text"
              name="name.first"
              placeholder="First Name"
              className="input"
              value={data.name.first}
              onChange={handleChange}
              required
            />
            {errors.first && <p className="error">{errors.first}</p>}
            {/* כאן יש להוסיף שדות נוספים לפי הצורך */}
            <button className="btn">Sign Up</button>
          </form>
        </div>
        {/* Login */}
        <div className="container__form container--signin">
          <form action="#" className="form" id="form2" onSubmit={handleSubmit2}>
            <h2 className="form__title">Sign In</h2>
            <input
              type="email"
              placeholder="Email"
              className="input"
              name="email"
              value={isLoginData.email}
              onChange={handleChange}
            />
            <input
              type="password"
              placeholder="Password"
              className="input"
              name="password"
              value={isLoginData.password}
              onChange={handleChange}
            />
            <button className="btn">Sign In</button>
          </form>
        </div>
        <div className="container__overlay">
          <div className="overlay">
            <div className="overlay__panel overlay--left">
              <button className="btn" id="signIn" onClick={handleSignInClick}>
                Sign In
              </button>
            </div>
            <div className="overlay__panel overlay--right">
              <button className="btn" id="signUp" onClick={handleSignUpClick}>
                Sign Up
              </button>
            </div>
          </div>
        </div>
      </div>
      <div className="gal">
        <div className="card">
          <h2>{cardData.title}</h2>
          <p>{cardData.subtitle}</p>
          <img src={cardData.image.url} alt={cardData.image.alt} />
          {/* ניתן להוסיף כאן פרטים נוספים לכרטיס אם רצית */}
        </div>
      </div>
    </div>
  );
};

export default SignInRegister;
