import React, { useState, useEffect } from "react";
import axios from "axios";

const SelfieUpload = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [message, setMessage] = useState("");
  const [uploadedSelfies, setUploadedSelfies] = useState([]);

  // Load the selfie from localStorage when the component mounts
  useEffect(() => {
    const storedSelfie = localStorage.getItem("selfie");
    if (storedSelfie) {
      setSelectedFile(JSON.parse(storedSelfie));
    }
  }, []);

  // Save the selfie to localStorage whenever it changes
  useEffect(() => {
    if (selectedFile) {
      localStorage.setItem("selfie", JSON.stringify(selectedFile));
    }
  }, [selectedFile]);

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setSelectedFile(file);
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!selectedFile) {
      setMessage("Please upload a selfie.");
      return;
    }

    const formData = new FormData();
    formData.append("image", selectedFile);

    try {
      const response = await axios.post(
        "http://localhost:5000/upload-selfie",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );

      setMessage(response.data.message);
      fetchSelfies(); // Fetch uploaded selfies after upload
    } catch (error) {
      console.error(error);
      setMessage("Error uploading selfie");
    }
  };

  // Fetch all uploaded selfies from the server
  const fetchSelfies = async () => {
    try {
      const response = await axios.get("http://localhost:5000/selfies");
      setUploadedSelfies(response.data);
    } catch (error) {
      console.error("Error fetching selfies:", error);
    }
  };

  useEffect(() => {
    fetchSelfies(); // Fetch selfies on component mount
  }, []);
  useEffect(() => {
    const fetchSelfies = async () => {
      try {
        const response = await axios.get("http://localhost:5000/selfies");
        setUploadedSelfies(response.data);
        localStorage.setItem("uploadedSelfies", JSON.stringify(response.data));
      } catch (error) {
        console.error("Error fetching selfies:", error);
      }
    };

    fetchSelfies();
  }, []);
  useEffect(() => {
    const storedSelfies = localStorage.getItem("uploadedSelfies");
    if (storedSelfies) {
      setUploadedSelfies(JSON.parse(storedSelfies));
    }
  }, []);

  return (
    <div style={{ paddingTop: "100px" }}>
      <h1>Upload Your Selfie</h1>
      <form onSubmit={handleUpload}>
        <input type="file" accept="image/*" onChange={handleFileChange} />
        <button type="submit">Upload</button>
      </form>
      {message && <p>{message}</p>}

      {/* Display uploaded selfies */}
      <h2>Uploaded Selfies</h2>
      <div style={{ display: "flex", flexWrap: "wrap" }}>
        {uploadedSelfies.map((selfie, index) => (
          <div key={index} style={{ margin: "10px" }}>
            <img
              src={`http://localhost:5000/images/${encodeURIComponent(
                selfie.filename
              )}`}
              alt={selfie.filename ? `Uploaded selfie: ${selfie.filename}` : ""}
              style={{ width: "150px", height: "150px", objectFit: "cover" }}
            />
            <p>{selfie.filename}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default SelfieUpload;
