import React, { useState } from "react";

const ToggleButton = () => {
  const [isToggle, setIsToggle] = useState(false);

  function handleClick() {
    setIsToggle(!isToggle);
  }
  return (
    <div>
      <h1>Toggle Button</h1>
      <button onClick={handleClick}>{isToggle ? "ON" : "OFF"}</button>
    </div>
  );
};

export default ToggleButton;
