import React, { useState } from "react";
import styles from "./CreateEvent.module.css";

const CreateEvent = () => {
  const [progressWidth, setProgressWidth] = useState(0);
  const [currentStep, setCurrentStep] = useState(1);
  const [completedSteps, setCompletedSteps] = useState([]);
  const handleStart = () => {
    if (currentStep < steps.length) {
      setProgressWidth(progressWidth + 250);

      // עדכון השלב הנוכחי ותחילת הצביעה של העיגול ברגע שה-progress מגיע לשלב
      setTimeout(() => {
        setCompletedSteps((prevSteps) => [...prevSteps, currentStep]);
        setCurrentStep((prevStep) => prevStep + 1); // עדכון השלב הנוכחי לשלב הבא
      }, 1000); // תזמון ל-1 שניה להתאמה לאנימציה
    }
  };
  const handleReset = () => {
    setProgressWidth(0);
  };

  const steps = ["1", "2", "3"];

  return (
    <div>
      <h4>Connect The Dots</h4>
      <div className={styles.progressContainer}>
        <ul>
          {steps.map((step, index) => (
            <li
              key={index}
              style={{
                backgroundColor:
                  index + 1 === currentStep ||
                  completedSteps.includes(index + 1)
                    ? "#ff1f35"
                    : "#fff",
                color:
                  index + 1 === currentStep ||
                  completedSteps.includes(index + 1)
                    ? "#fff"
                    : "#000",
                border:
                  index + 1 > currentStep && !completedSteps.includes(index + 1)
                    ? "2px solid #ff1f35"
                    : "none",
              }}
            >
              {step}
            </li>
          ))}
        </ul>
        <div
          className={styles.progressBar}
          style={{ width: `${progressWidth}px` }}
        ></div>
      </div>

      <div className={styles.button} onClick={handleStart}>
        Connect
      </div>
      <div
        className={`${styles.button} ${styles.resetButton}`}
        onClick={handleReset}
      >
        Reset
      </div>
    </div>
  );
};

export default CreateEvent;
