import React, { useEffect, useReducer } from "react";
import Child from "./Child";
import Counter from "./Counter";
import RandomFunction from "../radnomNumber/RandomFunction";

// Define action types
const actionTypes = {
  SET_FRUIT_LIST: "SET_FRUIT_LIST",
  TOGGLE_SHOW_LIST: "TOGGLE_SHOW_LIST",
  SET_VEG_LIST: "SET_VEG_LIST",
};

// Define reducer function
const reducer = (state, action) => {
  switch (action.type) {
    case actionTypes.SET_FRUIT_LIST:
      return { ...state, fruitList: action.payload };
    case actionTypes.TOGGLE_SHOW_LIST:
      return { ...state, showList: !state.showList };
    case actionTypes.SET_VEG_LIST:
      return { ...state, fruitList: action.payload };
    default:
      return state;
  }
};

const Father = () => {
  // Define initial state
  const initialState = {
    fruitList: ["apple", "pear", "banana"],
    showList: false,
  };

  // Use useReducer to manage state
  const [state, dispatch] = useReducer(reducer, initialState);

  // Destructure state
  const { fruitList, showList } = state;

  // Dummy fruit data
  const fruitData = ["apple", "pear", "banana"];

  useEffect(() => {
    // Initialize fruitList state with fruitData when the component mounts
    dispatch({ type: actionTypes.SET_FRUIT_LIST, payload: fruitData });
  }, [fruitData]);

  return (
    <div>
      <h1>Hello, I'm The Father</h1>

      <button onClick={() => dispatch({ type: actionTypes.TOGGLE_SHOW_LIST })}>
        {showList ? "Hide" : "Show"} List
      </button>

      {showList && (
        <Child
          name="Gal"
          fruitList={fruitList}
          updateVeg={(newVegList) =>
            dispatch({ type: actionTypes.SET_VEG_LIST, payload: newVegList })
          }
          updateFruit={(newFruitList) =>
            dispatch({
              type: actionTypes.SET_FRUIT_LIST,
              payload: newFruitList,
            })
          }
        />
      )}
      <Counter />
      <br />
      <RandomFunction />
    </div>
  );
};

export default Father;
