import React, { useState, useEffect } from "react";
import axios from "axios";
import FavoriteBorderIcon from "@mui/icons-material/FavoriteBorder";
import FavoriteIcon from "@mui/icons-material/Favorite";
import Loading from "../Loading/Loading";
import styleFindTheOne from "./FoundTheOne.module.css";

const FindTheOne = () => {
  const [cards, setCards] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [likedCards, setLikedCards] = useState([]);

  const apiEndpoint = "https://monkfish-app-z9uza.ondigitalocean.app/bcard2/cards";

  // Load liked cards from localStorage on component mount
  useEffect(() => {
    const storedLikedCards = JSON.parse(localStorage.getItem("likedCards")) || [];
    setLikedCards(storedLikedCards);
  }, []);

  // Save liked cards to localStorage whenever likedCards state changes
  useEffect(() => {
    localStorage.setItem("likedCards", JSON.stringify(likedCards));
  }, [likedCards]);

  useEffect(() => {
    const fetchCards = async () => {
      try {
        const response = await axios.get(apiEndpoint);
        setCards(response.data);
      } catch (error) {
        setError(error.message || "An error occurred while fetching cards.");
      } finally {
        setLoading(false);
      }
    };

    fetchCards();
  }, []);

  const handleLike = async (cardId) => {
    try {
      const token = localStorage.getItem("token");
      const config = {
        method: "patch",
        url: `${apiEndpoint}/${cardId}`,
        headers: {
          "x-auth-token": token,
        },
      };

      await axios(config);

      // Update likedCards state
      if (likedCards.includes(cardId)) {
        setLikedCards(likedCards.filter((id) => id !== cardId));
      } else {
        setLikedCards([...likedCards, cardId]);
      }
    } catch (error) {
      console.error("Error liking/unliking card:", error);
    }
  };

  if (loading) {
    return <Loading />;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div className="bodyFindTheOne">
      <div className={styleFindTheOne.container_cards}>
        <div className={styleFindTheOne.frontCard}>
          <h1>Cards</h1>
          <ul className={styleFindTheOne.listOfCards}>
            {cards.map((card) => (
              <li className={styleFindTheOne.item} key={card._id}>
                <img
                  className={styleFindTheOne.imgCards}
                  src={card.image.url}
                  alt={card.image.alt}
                />
                <h2>{card.title}</h2>
                <p className={styleFindTheOne.p}>{card.subtitle}</p>
                <p className={styleFindTheOne.p}>
                  {card.address && card.address.country}
                </p>
                <div onClick={() => handleLike(card._id)}>
                  {likedCards.includes(card._id) ? (
                    <FavoriteIcon style={{ color: "red" }} />
                  ) : (
                    <FavoriteBorderIcon />
                  )}
                </div>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default FindTheOne;
