import React, { useState } from "react";
import axios from "axios";
import "./Register.css";

const SignInRegister = () => {
  const [isSignIn, setIsSignIn] = useState(false);
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [phone, setPhone] = useState("");
  const [imageUrl, setImageUrl] = useState("");
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  const handleSignInClick = () => {
    setIsSignIn(false);
  };

  const handleSignUpClick = () => {
    setIsSignIn(true);
  };
  const validateEmail = (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const validatePassword = (password) => {
    const passwordRegex =
      /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[!@#$%^&*-]).{9,}$/;
    return passwordRegex.test(password);
  };

  const validatePhone = (phone) => {
    const phoneRegex = /^(\+972|0)?[5-9]\d{8}$/;
    return phoneRegex.test(phone);
  };

  const validateUrl = (url) => {
    const urlRegex = /^(https?|chrome):\/\/[^\s$.?#].[^\s]*$/gm;
    return urlRegex.test(url);
  };
  const handleSignUp = async (e) => {
    e.preventDefault();

    if (!validateEmail(email)) {
      setError("Invalid email format");
      return;
    }

    if (!validatePassword(password)) {
      setError(
        "Password must be at least nine characters long and contain an uppercase letter, a lowercase letter, a number and one of the following characters !@#$%^&*-"
      );
      return;
    }

    if (!validatePhone(phone)) {
      setError("Invalid Israeli phone number format");
      return;
    }

    if (!validateUrl(imageUrl)) {
      setError("Invalid URL format");
      return;
    }

    try {
      const response = await axios.post(
        "https://monkfish-app-z9uza.ondigitalocean.app/bcard2/users",
        {
          username,
          email,
          password,
          phone,
          imageUrl,
        }
      );
      if (response.status === 201) {
        setSuccess("User registered successfully");
        setError("");
      }
    } catch (err) {
      setError(
        "Registration failed: " + (err.response?.data?.message || err.message)
      );
      setSuccess("");
    }
  };

  return (
    <div className="body-img">
      <div className={`container ${isSignIn ? "right-panel-active" : ""}`}>
        <div className="container__form container--signup">
          <form className="form formScroll" id="form1">
            <h2 className="form__title">Sign Up</h2>
            {error && <p style={{ color: "red" }}>{error}</p>}
            {success && <p style={{ color: "green" }}>{success}</p>}
            <input
              type="text"
              placeholder="User"
              className="input"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
            <input
              type="email"
              placeholder="Email"
              className="input"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
            <input
              type="password"
              placeholder="Password"
              className="input"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            <input
              type="text"
              placeholder="Phone"
              className="input"
              value={phone}
              onChange={(e) => setPhone(e.target.value)}
              required
            />
            <input
              type="text"
              placeholder="Image URL"
              className="input"
              value={imageUrl}
              onChange={(e) => setImageUrl(e.target.value)}
              // required
            />
            <button className="btn" onSubmit={handleSignUp}>
              Sign Up
            </button>
          </form>
        </div>
        <div className="container__form container--signin">
          <form className="form" id="form2">
            <h2 className="form__title">Sign In</h2>
            {error && <p style={{ color: "red" }}>{error}</p>}
            {success && <p style={{ color: "green" }}>{success}</p>}
            <input
              type="email"
              placeholder="Email"
              className="input"
              // value={email}
              onChange={(e) => setEmail(e.target.value)}
              // required
            />
            <input
              type="password"
              placeholder="Password"
              className="input"
              // value={password}
              onChange={(e) => setPassword(e.target.value)}
              // required
            />
            <button className="btn">Sign In</button>
          </form>
        </div>

        <div className="container__overlay">
          <div className="overlay">
            <div className="overlay__panel overlay--left">
              <button className="btn" id="signIn" onClick={handleSignInClick}>
                Sign In
              </button>
            </div>
            <div className="overlay__panel overlay--right">
              <button className="btn" id="signUp" onClick={handleSignUpClick}>
                Sign Up
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SignInRegister;
