import React from "react";
import useCardsApi from "../../hooks/useCardsHooks/useCardsApi";

const FavoritCards = () => {
  const [cards] = useCardsApi();
  const userId = localStorage.getItem("user_id");

  const favoriteCards = cards.filter(card => card.likes.includes(userId));

  return (
    <div className="bodyAbout">
      <h1>Favorite Cards</h1>
      <ul>
        {favoriteCards.map((card) => (
          <li key={card._id}>
            <img src={card.image.url} alt={card.image.alt} />
            <h2>{card.title}</h2>
            <p>{card.subtitle}</p>
            {card.address && card.address.country && <p>{card.address.country}</p>}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default FavoritCards;
