// frontend/src/components/SelfieUpload.js
import React, { useState } from "react";
import axios from "axios";

const SelfieUpload = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [message, setMessage] = useState("");
  const [matchingImages, setMatchingImages] = useState([]);

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!selectedFile) {
      setMessage("Please upload a selfie.");
      return;
    }

    const formData = new FormData();
    formData.append("image", selectedFile);

    try {
      const response = await axios.post(
        "http://localhost:5000/upload-selfie", // Selfie upload endpoint
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );

      // Set message and display matched images
      setMessage(response.data.message);
      setMatchingImages(response.data.matchingImages);
    } catch (error) {
      console.error("Error uploading selfie:", error);
      setMessage("Error uploading selfie");
    }
  };

  return (
    <div style={{ paddingTop: "100px" }}>
      <h1>Upload Your Selfie</h1>
      <form onSubmit={handleUpload}>
        <input type="file" accept="image/*" onChange={handleFileChange} />
        <button type="submit">Upload</button>
      </form>
      {message && <p>{message}</p>}

      {/* Display matched images */}
      <h2>Matching Images</h2>
      <div style={{ display: "flex", flexWrap: "wrap" }}>
        {matchingImages.map((image, index) => (
          <div key={index} style={{ margin: "10px" }}>
            <img
              src={`http://localhost:5000/images/${encodeURIComponent(
                image.filename
              )}`}
              alt={image.filename ? `Uploaded image: ${image.filename}` : ""}
              style={{ width: "150px", height: "150px", objectFit: "cover" }}
            />
            <p>{image}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default SelfieUpload;
