// component/Momentimg/ImageUpload.jsx

import React, { useEffect, useState } from "react";
import axios from "axios";

const ImageUpload = () => {
  const [images, setImages] = useState([]);
  const [uploadedImages, setUploadedImages] = useState([]);

  // הבאת התמונות הקיימות מהשרת
  useEffect(() => {
    const fetchImages = async () => {
      try {
        const response = await axios.get("http://localhost:5959/images");
        setUploadedImages(response.data);
      } catch (error) {
        console.error("Error fetching images:", error);
      }
    };

    fetchImages();
  }, []);

  const handleFileChange = (e) => {
    const files = e.target.files;
    const filesWithPath = Array.from(files).map((file) => ({
      file,
      relativePath: file.webkitRelativePath,
    }));
    setImages(filesWithPath);
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    const formData = new FormData();

    images.forEach((imageObj) => {
      formData.append("images", imageObj.file, imageObj.relativePath);
    });

    try {
      const response = await axios.post(
        "http://localhost:5959/upload",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );
      alert(response.data);
      setImages([]); // ריקון רשימת התמונות לאחר העלאה מוצלחת

      // עדכון רשימת התמונות המועלות לאחר ההעלאה
      const updatedImages = await axios.get("http://localhost:5959/images");
      setUploadedImages(updatedImages.data);
    } catch (error) {
      console.error(error);
      alert("Error uploading images");
    }
  };

  return (
    <div>
      <h1>Upload Images</h1>
      <form onSubmit={handleUpload}>
        <input
          type="file"
          webkitdirectory="true"
          multiple
          onChange={handleFileChange}
        />
        <button type="submit">Upload</button>
      </form>

      <h2>Uploaded Images</h2>
      <div style={{ display: "flex", flexWrap: "wrap" }}>
        {uploadedImages.map((image) => (
          <div key={image._id} style={{ margin: "10px" }}>
            <img
              src={`http://localhost:5959/${image.path}`}
              alt={image.filename}
              style={{ width: "150px", height: "150px", objectFit: "cover" }}
            />
            <p>{image.filename}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ImageUpload;
