import React, { useState } from "react";

const ViewTextInput = () => {
  const [isValue, setIsValue] = useState();

  function handleChange(event) {
    setIsValue(isValue.value);
  }
  return (
    <div>
      <input type="text" onChange={handleChange} />
      <p>{isValue}</p>
    </div>
  );
};

export default ViewTextInput;
