import React, { useContext, useState } from "react";
import { Link } from "react-router-dom";
import SunIcon from "../Navbar/Sunny.png";
import MoonIcon from "../Navbar/Moon.png";
import SearchIcon from "../Navbar/SearchIcon.png";
import Jobs from "../Navbar/Jobs.jpg";
import { ThemeContext } from "../../hooks/DarkMode/DarkModeContext";
import "../Navbar/Navbar.css";

function NavBar() {
  const { isSun, handleIconClick } = useContext(ThemeContext);
  const [isExpanded, setIsExpanded] = useState(false);
  // const searchInputRef = useRef(null);

  const handleSearchIconClick = () => {
    setIsExpanded(!isExpanded);
  };
  return (
    <header>
      <nav className="container_nav" src={isSun ? MoonIcon : SunIcon}>
        <div>
          <img className="logo" src={Jobs} alt="jobs" />
          <span>
            <big style={{ fontSize: "22px", color: "turquoise" }}>R</big>each
            your Destaney{" "}
          </span>
        </div>
        <ul className="link_list">
          <li>
            <Link className="link" to={"/"}>
              About
            </Link>
          </li>

          <li>
            <Link className="link" to={"/favCards"}>
              Favorit Cards
            </Link>
          </li>
          <li>
            <Link className="link" to={"/myCards"}>
              My Cards
            </Link>
          </li>

          <li className="left">
            <div className="searchBar">
              <input
                // ref={searchInputRef}
                className={isExpanded ? "expanded searchInput" : "searchInput"}
                type="text"
                placeholder="Search Your Card..."
              />
              <img
                src={SearchIcon}
                alt="Search Icon"
                onClick={handleSearchIconClick}
                className={
                  isExpanded ? "searchIcon searchIconMoved" : "searchIcon"
                }
              />
            </div>
          </li>
          <li>
            <img
              src={isSun ? SunIcon : MoonIcon}
              alt="Sun Icon"
              onClick={handleIconClick}
              className="moon_sun_icon"
            />
          </li>
          {/* <li>
            <Link className="link" to={"/login"}>
              Login
            </Link>
          </li> */}
          <li>
            <Link className="link" to={"/register"}>
              Register
            </Link>
          </li>
        </ul>
      </nav>
    </header>
  );
}

export default NavBar;
