import React, { useContext, useState } from "react";
import { Link } from "react-router-dom";
import SunIcon from "../Navbar/Sunny.png";
import MoonIcon from "../Navbar/Moon.png";
import SearchIcon from "../Navbar/SearchIcon.png";
import { ThemeContext } from "../../hooks/DarkMode/DarkModeContext"; // הייבוא של הקונטקסט
import StyleNavbar from "../Navbar/Navbar.module.css";

function NavBar() {
  const { isSun, handleIconClick } = useContext(ThemeContext);
  const [isExpanded, setIsExpanded] = useState(false);
  // const searchInputRef = useRef(null);

  const handleSearchIconClick = () => {
    setIsExpanded(!isExpanded);
  };
  return (
    <header>
      <nav
        className={StyleNavbar.container_nav}
        src={isSun ? MoonIcon : SunIcon}
      >
        <ul className={StyleNavbar.link_list}>
          <li>
            <Link className={StyleNavbar.link} to={"/about"}>
              About
            </Link>
          </li>
          <li>
            <Link className={StyleNavbar.link} to={"/Cards"}>
              Cards
            </Link>
          </li>
          <li>
            <Link className={StyleNavbar.link} to={"/favCards"}>
              Favorit Cards
            </Link>
          </li>
          <li>
            <Link className={StyleNavbar.link} to={"/login"}>
              Login
            </Link>
          </li>
          <li>
            <Link className={StyleNavbar.link} to={"/register"}>
              Register
            </Link>
          </li>
          <li>
            <div className={StyleNavbar.searchBar}>
              <input
                // ref={searchInputRef}
                className={`${isExpanded ? StyleNavbar.expanded : ""} ${
                  StyleNavbar.searchInput
                }`}
                type="text"
                placeholder="Search Your Card..."
              />
              <img
                src={SearchIcon}
                alt="Search Icon"
                onClick={handleSearchIconClick}
                className={`${isExpanded ? StyleNavbar.searchIconMoved : ""} ${
                  StyleNavbar.searchIcon
                }`}
              />
            </div>
          </li>
          <li>
            <img
              src={isSun ? SunIcon : MoonIcon}
              alt="Sun Icon"
              onClick={handleIconClick}
              className={StyleNavbar.moon_sun_icon}
            />
          </li>
        </ul>
      </nav>
    </header>
  );
}

export default NavBar;
