import React, { useState } from "react";
import styles from "./CreateEvent.module.css";

const CreateEvent = () => {
  const MAX_STEPS = 4; // Changed to 3 steps
  const [currentStep, setCurrentStep] = useState(1);

  const handleNext = () => {
    if (currentStep < MAX_STEPS) {
      setCurrentStep((prevStep) => prevStep + 1);
    }
  };

  const handlePrevious = () => {
    if (currentStep > 1) {
      setCurrentStep((prevStep) => prevStep - 1);
    }
  };

  const handleFinish = () => {
    window.location.reload();
  };

  return (
    <div className={styles.container}>
      <div className={styles.stepProgressBar}>
        {[...Array(MAX_STEPS)].map((_, index) => (
          <div key={index} className={styles.step}>
            <p className={styles.stepText}>Step {index + 1}</p>
            <div
              className={`${styles.bullet} ${
                currentStep > index + 1 ? styles.completed : ""
              }`}
            >
              {index + 1}
            </div>
          </div>
        ))}
      </div>
      <div className={styles.main}>
        <p className={`${styles.content} ${styles.textCenter}`}>
          Step Number {currentStep}
        </p>
        <button
          className={styles.previousBtn}
          onClick={handlePrevious}
          disabled={currentStep === 1}
        >
          Previous
        </button>
        <button
          className={styles.nextBtn}
          onClick={handleNext}
          disabled={currentStep === MAX_STEPS}
        >
          Next
        </button>
        <button
          className={styles.finishBtn}
          onClick={handleFinish}
          disabled={currentStep !== MAX_STEPS}
        >
          Finish
        </button>
      </div>
    </div>
  );
};

export default CreateEvent;
