// src/hooks/UserHooks/userContextApp.js

import React, { createContext, useState, useEffect, useContext } from "react";

export const UserContext = createContext();

export const UserProvider = ({ children }) => {
  const [userInformation, setUserInformation] = useState(
    JSON.parse(localStorage.getItem("userInformation")) || null
  );
  const [likedCards, setLikedCards] = useState(
    JSON.parse(localStorage.getItem("likedCards")) || []
  );

  useEffect(() => {
    if (userInformation) {
      localStorage.setItem("userInformation", JSON.stringify(userInformation));
    }
  }, [userInformation]);

  useEffect(() => {
    localStorage.setItem("likedCards", JSON.stringify(likedCards));
  }, [likedCards]);

  const addLikedCard = (cardId) => {
    if (!likedCards.includes(cardId)) {
      setLikedCards([...likedCards, cardId]);
    }
  };

  const removeLikedCard = (cardId) => {
    setLikedCards(likedCards.filter((id) => id !== cardId));
  };

  return (
    <UserContext.Provider
      value={{ userInformation, setUserInformation, likedCards, addLikedCard, removeLikedCard }}
    >
      {children}
    </UserContext.Provider>
  );
};

export const useUserContext = () => useContext(UserContext);
