import React from 'react';
import useGetOneCard from '../hooks/useCardsHooks/useGetOneCard'; // הכנס את הנתיב המתאים ל-hook של useGetOneCard

const Bla = ({ cardId }) => {
  const { cardData, loading, error } = useGetOneCard(cardId); // ה־cardId של הכרטיס הרצוי

  if (loading) {
    return <p>Loading...</p>; // מציג הודעת טעינה כל עוד הנתונים נטענים
  }

  if (error) {
    return <p>Error: {error}</p>; // מציג הודעת שגיאה במקרה של כישלון בבקשה
  }

  if (!cardData) {
    return <p>No card data available.</p>; // מציג הודעה אם אין נתונים להצגה
  }

  return (
    <div className="card">
      <h2>{cardData.title}</h2>
      <p>{cardData.subtitle}</p>
      <img src={cardData.image.url} alt={cardData.image.alt} />
      {/* נוסיף כמה פרטים נוספים לכרטיס אם רצית */}
    </div>
  );
};

export default Bla;
