// ThemeContext.js
import React, { createContext, useState, useEffect } from "react";

export const ThemeContext = createContext();

export const ThemeProvider = ({ children }) => {
  const [isSun, setIsSun] = useState(() => {
    // בדיקה אם קיימת הגדרה ב-Local Storage
    const savedTheme = localStorage.getItem("theme");
    return savedTheme === "dark" ? true : false;
  });

  const handleIconClick = () => {
    setIsSun(!isSun);
  };

  useEffect(() => {
    if (isSun) {
      document.body.classList.add("dark-mode");
      document.querySelector(".container_nav").classList.add("dark-mode");
      localStorage.setItem("theme", "dark"); // שמירת ההגדרה ב-Local Storage
    } else {
      document.body.classList.remove("dark-mode");
      document.querySelector(".container_nav").classList.remove("dark-mode");
      localStorage.setItem("theme", "light"); // שמירת ההגדרה ב-Local Storage
    }
  }, [isSun]);

  return (
    <ThemeContext.Provider value={{ isSun, handleIconClick }}>
      {children}
    </ThemeContext.Provider>
  );
};
