import React, { useEffect, useState } from "react";
import axios from "axios";

const ImageUpload = () => {
  const [images, setImages] = useState([]);
  const [uploadedImages, setUploadedImages] = useState([]);

  // Fetch existing images from the server
  useEffect(() => {
    const fetchImages = async () => {
      try {
        const response = await axios.get("http://localhost:5000/images");
        setUploadedImages(response.data);
      } catch (error) {
        console.error("Error fetching images:", error);
      }
    };

    fetchImages();
  }, []);

  const handleFileChange = (e) => {
    const files = e.target.files;
    const filesWithPath = Array.from(files).map((file) => ({
      file,
      relativePath: file.webkitRelativePath,
    }));
    setImages(filesWithPath);
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    const formData = new FormData();

    images.forEach((imageObj) => {
      formData.append("images", imageObj.file, imageObj.relativePath);
    });

    try {
      const response = await axios.post(
        "http://localhost:5000/upload",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );
      console.log(response.data); // בדוק את התשובה
      alert(response.data);
    } catch (error) {
      console.error("Error uploading images", error); // הצג את השגיאה המלאה
      alert("Error uploading images");
    }
  };

  const handleDeleteAll = async () => {
    try {
      await axios.delete("http://localhost:5000/delete-all-images");
      setUploadedImages([]); // Clear the displayed images list
      alert("All images deleted successfully");
    } catch (error) {
      console.error("Error deleting images:", error);
      alert("Error deleting images");
    }
  };

  return (
    <div style={{ paddingTop: "100px" }}>
      <h1>Upload Images</h1>
      <form onSubmit={handleUpload}>
        <input
          type="file"
          webkitdirectory="true"
          multiple
          onChange={handleFileChange}
        />
        <button type="submit">Upload</button>
      </form>

      <button onClick={handleDeleteAll} style={{ marginTop: "20px" }}>
        Delete All Images
      </button>

      <h2>Uploaded Images</h2>
      <div style={{ display: "flex", flexWrap: "wrap" }}>
        {uploadedImages.map((image) => (
          <div key={image._id} style={{ margin: "10px" }}>
            <img
              src={`http://localhost:5000/${image.path}`}
              alt={image.filename}
              style={{ width: "150px", height: "150px", objectFit: "cover" }}
            />
            <p>{image.filename}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ImageUpload;
