import React, { useState } from "react";
import axios from "axios";
import "./Register.css";

const SignInRegister = () => {
  const [isSignIn, setIsSignIn] = useState(false);
  const [data, setIsData] = useState({
    name: { first: "", middle: "", last: "" },
    phone: "",
    email: "",
    password: "",
    image: { url: "", alt: "" },
    address: {
      state: "",
      country: "",
      city: "",
      street: "",
      houseNumber: "",
      zip: "",
    },
    isBusiness: false,
    isAdmin: false,
  });
  const [errors, setErrors] = useState({});

  const handleSignInClick = () => {
    setIsSignIn(false);
  };

  const handleSignUpClick = () => {
    setIsSignIn(true);
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    const updatedValue = type === "checkbox" ? checked : value;

    setIsData((prevData) => {
      const keys = name.split(".");
      if (keys.length > 1) {
        return {
          ...prevData,
          [keys[0]]: {
            ...prevData[keys[0]],
            [keys[1]]: updatedValue,
          },
        };
      } else {
        return {
          ...prevData,
          [name]: updatedValue,
        };
      }
    });
  };

  const validate = () => {
    const newErrors = {};

    const validateField = (key, value) => {
      switch (key) {
        case "name.first":
          if (!value || value.length < 2 || value.length > 256) {
            newErrors.first =
              "First name must be between 2 and 256 characters.";
          }
          break;
        case "name.middle":
          if (value && (value.length < 2 || value.length > 256)) {
            newErrors.middle =
              "Middle name must be between 2 and 256 characters.";
          }
          break;
        case "name.last":
          if (!value || value.length < 2 || value.length > 256) {
            newErrors.last = "Last name must be between 2 and 256 characters.";
          }
          break;
        case "phone":
          if (!value || value.length < 9 || value.length > 11) {
            newErrors.phone =
              "Phone number must be between 9 and 11 characters.";
          }
          break;
        case "email":
          if (!value || value.length < 5) {
            newErrors.email = "Email must be at least 5 characters long.";
          }
          break;
        case "password":
          if (!value || value.length < 7 || value.length > 20) {
            newErrors.password =
              "Password must be between 7 and 20 characters.";
          }
          break;
        case "image.url":
          if (value && value.length < 14) {
            newErrors.url = "Image URL must be at least 14 characters long.";
          }
          break;
        case "image.alt":
          if (value && (value.length < 2 || value.length > 256)) {
            newErrors.alt =
              "Image description must be between 2 and 256 characters.";
          }
          break;
        case "address.state":
          if (value && (value.length < 2 || value.length > 256)) {
            newErrors.state = "State must be between 2 and 256 characters.";
          }
          break;
        case "address.country":
          if (!value || value.length < 2 || value.length > 256) {
            newErrors.country = "Country must be between 2 and 256 characters.";
          }
          break;
        case "address.city":
          if (!value || value.length < 2 || value.length > 256) {
            newErrors.city = "City must be between 2 and 256 characters.";
          }
          break;
        case "address.street":
          if (!value || value.length < 2 || value.length > 256) {
            newErrors.street = "Street must be between 2 and 256 characters.";
          }
          break;
        case "address.houseNumber":
          if (!value || value < 2 || value > 256) {
            newErrors.houseNumber = "House number must be between 2 and 256.";
          }
          break;
        case "isBusiness":
          if (typeof value !== "boolean") {
            newErrors.isBusiness = "Business status must be specified.";
          }
        case "isAdmin":
          if (typeof value !== "boolean") {
            newErrors.isAdmin = "Admin status must be specified.";
          }
          break;
        default:
          break;
      }
    };

    Object.keys(data).forEach((key) => {
      if (typeof data[key] === "object" && !Array.isArray(data[key])) {
        Object.keys(data[key]).forEach((subKey) => {
          validateField(`${key}.${subKey}`, data[key][subKey]);
        });
      } else {
        validateField(key, data[key]);
      }
    });

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validate()) {
      console.log("Validation failed");
      return;
    }
    // Post Login
    var configLoding = {
      method: "post",
      maxBodyLength: Infinity,
      url: "https://monkfish-app-z9uza.ondigitalocean.app/bcard2/users/login",
      headers: {},
      data: data,
    };
    try {
      const response = await axios(configLoding);
      console.log(response);
    } catch (error) {
      console.error(error);
    }
    //Post Register
    const configRegister = {
      method: "post",
      maxBodyLength: Infinity,
      url: "https://monkfish-app-z9uza.ondigitalocean.app/bcard2/users",
      headers: {},
      data: data,
    };

    try {
      const response = await axios(configRegister);
      console.log(response);
    } catch (error) {
      console.error(error);
    }
  };
  console.log(data);
  return (
    <div className="body-img">
      <div className={`container ${isSignIn ? "right-panel-active" : ""}`}>
        <div className="container__form container--signup">
          <form
            action="#"
            className="form formScroll"
            id="form1"
            onSubmit={handleSubmit}
          >
            <h2 className="form__title">Sign Up</h2>
            <input
              type="text"
              name="name.first"
              placeholder="First Name"
              className="input"
              value={data.name.first}
              onChange={handleChange}
              required
            />
            {errors.first && <p className="error">{errors.first}</p>}
            <input
              type="text"
              name="name.last"
              placeholder="Last Name"
              className="input"
              value={data.name.last}
              onChange={handleChange}
              required
            />
            {errors.last && <p className="error">{errors.last}</p>}
            <input
              name="email"
              type="email"
              placeholder="Email"
              className="input"
              value={data.email}
              onChange={handleChange}
              required
            />
            {errors.email && <p className="error">{errors.email}</p>}
            <input
              name="phone"
              type="text"
              placeholder="Phone"
              className="input"
              value={data.phone}
              onChange={handleChange}
              required
            />
            {errors.phone && <p className="error">{errors.phone}</p>}
            <input
              type="password"
              name="password"
              placeholder="Password"
              className="input"
              value={data.password}
              onChange={handleChange}
              required
            />
            {errors.password && <p className="error">{errors.password}</p>}
            <input
              name="image.url"
              type="text"
              placeholder="Image Url"
              className="input"
              value={data.image.url}
              onChange={handleChange}
            />
            {errors.url && <p className="error">{errors.url}</p>}
            <input
              name="image.alt"
              type="text"
              placeholder="Image Description"
              className="input"
              value={data.image.alt}
              onChange={handleChange}
            />
            {errors.alt && <p className="error">{errors.alt}</p>}
            <input
              name="address.state"
              type="text"
              placeholder="State"
              className="input"
              value={data.address.state}
              onChange={handleChange}
            />
            {errors.state && <p className="error">{errors.state}</p>}
            <input
              name="address.country"
              type="text"
              placeholder="Country"
              className="input"
              value={data.address.country}
              onChange={handleChange}
              required
            />
            {errors.country && <p className="error">{errors.country}</p>}
            <input
              name="address.city"
              type="text"
              placeholder="City"
              className="input"
              value={data.address.city}
              onChange={handleChange}
              required
            />
            {errors.city && <p className="error">{errors.city}</p>}
            <input
              name="address.street"
              type="text"
              placeholder="Street"
              className="input"
              value={data.address.street}
              onChange={handleChange}
              required
            />
            {errors.street && <p className="error">{errors.street}</p>}
            <input
              name="address.houseNumber"
              type="text"
              placeholder="House Number"
              className="input"
              value={data.address.houseNumber}
              onChange={handleChange}
              required
            />
            {errors.houseNumber && (
              <p className="error">{errors.houseNumber}</p>
            )}
            <input
              name="address.zip"
              type="text"
              placeholder="ZIP Code"
              className="input"
              value={data.address.zip}
              onChange={handleChange}
              required
            />
            {/* {errors.zip && <p className="error">{errors.zip}</p>} */}
            <input
              name="isBusiness"
              checked={data.isBusiness}
              type="checkbox"
              className="checkbox"
              onChange={handleChange}
              required
            />
            {errors.isBusiness && <p className="error">{errors.isBusiness}</p>}
            <input
              name="isAdmin"
              checked={data.isAdmin}
              type="checkbox"
              className="checkbox"
              onChange={handleChange}
              required
            />
            {errors.isAdmin && <p className="error">{errors.isAdmin}</p>}
            <button className="btn">Sign Up</button>
          </form>
        </div>

        <div className="container__form container--signin">
          <form action="#" className="form" id="form2" onSubmit={handleSubmit}>
            <h2 className="form__title">Sign In</h2>
            <input type="email" placeholder="Email" className="input" />
            <input type="password" placeholder="Password" className="input" />
            <button className="btn">Sign In</button>
          </form>
        </div>

        <div className="container__overlay">
          <div className="overlay">
            <div className="overlay__panel overlay--left">
              <button className="btn" id="signIn" onClick={handleSignInClick}>
                Sign In
              </button>
            </div>
            <div className="overlay__panel overlay--right">
              <button className="btn" id="signUp" onClick={handleSignUpClick}>
                Sign Up
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SignInRegister;
